mapper.select:
select id 
from member 
where id = #{ id }

select email 
from member 
where email = #{ email }

select id
from member
where name=#{name} and email=#{email}

select pass
from member
where id= #{ id } and name = #{ name} and email = #{ email }

update member
set pass=#{new_pass}
where id=#{id}
